import { connect } from "react-redux";
import propTypes from 'prop-types';
import { Card, CardColumns, ListGroup } from "react-bootstrap";
import Header from "../components/Header";

function ShoppingCart({itemsInShoppingCart}) {
  return (
    <section>
      <Header />
      <CardColumns>
        <Card>
          <Card.Header>Itens no carrinho: {itemsInShoppingCart.length}</Card.Header>
          <ListGroup variant="flush">
            {
              itemsInShoppingCart.map((item) => {
                console.log('oi');
                return (
                  <ListGroup.Item key={item.id}>
                    <img src={item.thumbnail} alt={item.title} width="50px" height="50px"/>
                    <p>{item.title}</p>
                    <p>Quantidade: 1</p>
                    <p>R$ {item.price.toFixed(2)}</p>
                  </ListGroup.Item>
                )
              })
            }
          </ListGroup>
        </Card>
      </CardColumns>
    </section>
  )
}

const mapStateToProps = (state) => ({
  itemsInShoppingCart: state.market.payload.productsInShoppingCart
});


ShoppingCart.propTypes = {
  itemsInShoppingCart: propTypes.arrayOf(propTypes.object),
};

ShoppingCart.defaultProps = {
  itemsInShoppingCart: []
};

export default connect(mapStateToProps)(ShoppingCart);
