import { Card } from "react-bootstrap";
import propTypes from 'prop-types';

import '../styles/Product.css';

function Product({ item }) {
  return (
    <Card className="product__card" style={{borderRadius: '30px', backgroundColor: '#f8f9fa'}}>
      <Card.Img className="product__image" variant="top" src={item.thumbnail} alt={item.title}/>
      <Card.Body>
        <Card.Title 
          className="product__title" 
          style={{fontSize: '1rem', textAlign: 'center'}}
        >
          {item.title}
        </Card.Title>
        <Card.Text className="product__price" >R$ {item.price}</Card.Text>
      </Card.Body>
    </Card>
  )
}

Product.propTypes = {
  item: propTypes.shape({
    thumbnail: propTypes.string.isRequired,
    title: propTypes.string.isRequired,
    price: propTypes.number.isRequired,
  }).isRequired,
};



export default Product;
