import { useEffect, useState } from 'react';
import { Button } from 'react-bootstrap';
import { unmountComponentAtNode } from 'react-dom';

import '../styles/ButtonToTop.css';


// idéia retirada no site: https://www.geeksforgeeks.org/how-to-create-a-scroll-to-top-button-in-react-js/

function ButtonToTop() {
	const [visible, setVisible] = useState(false);

useEffect(() => {
  function toggleVisible() {
    const scrolled = document.documentElement.scrollTop;

    if (scrolled > 300) {
      setVisible(true);
    } else if (scrolled <= 300) {
      setVisible(false);
    }
  }
    window.addEventListener('scroll', toggleVisible);
  return () => {
    setVisible(false);
    unmountComponentAtNode(ButtonToTop)
  }
}, [])




	function handleClick() {
		window.scrollTo({
			top: 0,
			behavior: 'smooth',
		});
	}



	return (
		<section className="top-button">
			<Button
				type="button"
				onClick={() => handleClick()}
				style={{
					backgroundColor: 'white',
					border: 'none',
					display: visible ? 'inline' : 'none',
				}}
			>
				<img
					src="https://img.icons8.com/ios/50/000000/double-up.png"
					alt="top arrow"
				/>
			</Button>
		</section>
	);
}

export default ButtonToTop;
