import { Button, ButtonGroup } from "react-bootstrap";
import propTypes from 'prop-types';

function ListCategories({categories}) {
  const NUMBER_OF_CATEGORIES = 12;

  return (
    <ButtonGroup className="categories" style={{overflowX:'scroll'}}>
      {
        categories.slice(0,NUMBER_OF_CATEGORIES).map((category) => 
          <Button
            key={category.id}
            type="button"
            variant="light"
            style={{ backgroundColor: 'rgb(204, 79, 79)', border: 'none', marginRight:'5px', borderRadius: '30px', fontSize: '0.5rem'}}
          >
              {category.name}
          </Button>
        )
      }
    </ButtonGroup>
  )
}


ListCategories.propTypes = {
  categories: propTypes.arrayOf(propTypes.object)
}

ListCategories.defaultProps = {
  categories: []
}
export default ListCategories;
